package com.wipro.basic;

public class Evenodd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 10;

        if (num % 2 == 0) {
            System.out.println("The number is even.");
        } else {
            System.out.println("The number is odd.");
        }

	}

}
