package com.wipro.basic;

public class Ex9 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Even numbers from 0 to 10:");

        for (int i = 0; i <= 10; i++) {
            if (i % 2 == 0) {
                System.out.println(i);
            }
        }

	}

}
